import React, {useState} from "react";

function App () {

const [item, setItem] = useState("");
const [heading, setHeading] = useState("");

function ItemAdd(event) {

 const {name, value} = event.target; 

    //setItem(event.target.value)
setItem(prevValue => {
   
    if (item === "name") {
        return{
            item1: value,
            item2: prevValue.item2,
            item3: prevValue.item3,
            item4: prevValue.item4
        }
    }
     




});

}

function ClickAddButton(event) {
    setHeading(item)
}

    return (
        <div className="container">
            <div className="heading">
            <h1>To Do List</h1>
        </div>

        <div className="form">
          <input onChange={ItemAdd} 
          type= "text"
          value={item}
          />
          <button>
              <span
              onClick={ClickAddButton}>Add</span>
          </button>
            </div>

            <div>
            <ul>
                <li name = "item1" onChange={ItemAdd} value={heading}>A Item 
                </li>
               
            </ul>
            </div>
            </div>
    );
}







export default App;
