import React, {useState} from "react";

function App () {

const [item, setItem] = useState("");
const [heading, setHeading] = useState("");

function ItemAdd(event) {

 const {ItemName, value} = event.target;


    setItem(prevValue => {
        
        if (ItemName === item) {
            return {
                item: value,
            };
        }




    })
}

function ClickAddButton(event) {
    setHeading(item)
}



    return (
        <div className="container">
            <div className="heading">
            <h1>To Do List</h1>
        </div>

        <div className="form">
          <input onChange={ItemAdd} 
          type= "text"
          value={item}
          />
          <button>
              <span
              onClick={ClickAddButton}>Add</span>
          </button>
            </div>

            <div>
            <ul>
                <li>A Item {heading}</li>
            </ul>
            </div>
            </div>
    );
}







export default App;
