import React, {useState} from "react";
import ToDoItem from "./ToDoItem";

function App () {

const [inputItem, setInputItem] = useState("");
const [toDoItems, setItems] = useState([]);

function ItemAdd(event) {

 const newValue = event.target.value; 

setInputItem(newValue);

}

function ClickAddButton(event) {
    setItems((prevValue) => {
        return [...prevValue, inputItem]
    
    })
       setInputItem("");
}

    return (
        <div className="container">
            <div className="heading">
            <h1>To Do List</h1>
        </div>

        <div className="form">
          <input onChange={ItemAdd} 
          type= "text"
          value={inputItem}
          />
          <button>
              <span
              onClick={ClickAddButton}>Add</span>
          </button>
            </div>

            <div>
            <ul>
                {toDoItems.map(item => (<toDoItems text={toDoItems}/> ))}
               
            </ul>
            </div>
            </div>
    );
}

export default App;
